mapper.update:
if(originalFilePath != null) {
	update notice set noticetitle = #{noticeTitle},
		noticecontent = #{noticeContent},
			original_filepath = #{originalFilePath},
			rename_filepath = #{renameFilePath} 
			where noticeno = #{noticeNo} 
	} else { 
	update notice set noticetitle = #{noticeTitle},
			noticecontent = #{noticeContent} 			
			where noticeno = #{noticeNo}
	}
